function ExtractionQueue() {
  const [quickDumps, setQuickDumps] = useState<QuickDump[]>([]);
  const [processing, setProcessing] = useState<string | null>(null);

  useEffect(() => {
    loadQuickDumps();
  }, []);

  const loadQuickDumps = async () => {
    const result = await window.api.quickDumps.getAll();
    if (result.data) {
      setQuickDumps(result.data.filter(qd => qd.extractionStatus === 'pending'));
    }
  };

  const handleProcess = async (qd: QuickDump) => {
    setProcessing(qd.id);
    // Open extraction modal with pre-populated content
    // After extraction completes:
    await window.api.quickDumps.update(qd.id, { 
      extractionStatus: 'completed',
      processedAt: new Date().toISOString()
    });
    loadQuickDumps(); // Refresh list
  };

  return (
    <div className="p-6">
      <h1>Extraction Queue</h1>
      {quickDumps.length === 0 ? (
        <EmptyState />
      ) : (
        quickDumps.map(qd => (
          <QuickDumpItem key={qd.id} data={qd} onProcess={() => handleProcess(qd)} />
        ))
      )}
    </div>
  );
}
```

