interface NoteViewerProps {
  noteId?: string;
  note?: Note;
  onClose: () => void;
}

function NoteViewer({ noteId, note: initialNote, onClose }: NoteViewerProps) {
  const [note, setNote] = useState<Note | null>(initialNote || null);
  const [cards, setCards] = useState<CardWithFSRS[]>([]);
  const [loading, setLoading] = useState(!initialNote);

  useEffect(() => {
    const loadNote = async () => {
      if (noteId && !initialNote) {
        const result = await window.api.notes.getById(noteId);
        if (result.data) {
          setNote(result.data);
          // Fetch all derived cards
          const cardResults = await Promise.all(
            result.data.cardIds.map(id => window.api.cards.getById(id))
          );
          setCards(cardResults.filter(r => r.data).map(r => r.data!));
        }
        setLoading(false);
      }
    };
    loadNote();
  }, [noteId, initialNote]);

  const handleDeleteNote = async () => {
    if (note && confirm('Delete this note? Cards will be orphaned.')) {
      await window.api.notes.delete(note.id);
      onClose();
    }
  };

  return (
    <Dialog open onOpenChange={onClose}>
      <DialogContent className="max-w-3xl max-h-[80vh]">
        <DialogHeader>
          <DialogTitle>{note?.title}</DialogTitle>
          <span className="text-sm text-muted-foreground">
            {note?.createdAt && formatDate(note.createdAt)}
          </span>
        </DialogHeader>

        <ScrollArea className="h-full">
          {/* Content section */}
          <div className="prose">
            <p>{note?.content}</p>
          </div>

          {/* Tags */}
          <div className="flex gap-2 mt-4">
            {note?.tags.map(tag => <Badge key={tag}>{tag}</Badge>)}
          </div>

          {/* Derived cards */}
          <div className="mt-6">
            <h3>Derived Cards ({cards.length})</h3>
            <div className="grid grid-cols-2 gap-4">
              {cards.map(card => (
                <Card key={card.id}>
                  <CardHeader>
                    <Badge>{card.cardType}</Badge>
                  </CardHeader>
                  <CardContent>
                    <p className="truncate">{card.front}</p>
                    <Button onClick={() => reviewCard(card)}>Review now</Button>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        </ScrollArea>

        <DialogFooter>
          <Button variant="destructive" onClick={handleDeleteNote}>Delete Note</Button>
          <Button onClick={onClose}>Close</Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}
```

