function SearchBar() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<SearchResult | null>(null);
  const [focusedIndex, setFocusedIndex] = useState(0);

  // Debounced search
  useEffect(() => {
    if (!query.trim()) {
      setResults(null);
      return;
    }

    const timeoutId = setTimeout(async () => {
      const result = await window.api.search.query(query);
      if (result.data) {
        setResults(result.data);
      }
    }, 150);

    return () => clearTimeout(timeoutId);
  }, [query]);

  // Global Cmd+F shortcut
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'f') {
        e.preventDefault();
        searchInputRef.current?.focus();
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!results) return;
    const totalResults = results.cards.length + results.notes.length;

    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setFocusedIndex((prev) => (prev + 1) % totalResults);
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setFocusedIndex((prev) => (prev - 1 + totalResults) % totalResults);
    } else if (e.key === 'Enter' && focusedIndex >= 0) {
      // Navigate to selected result
      navigateToResult(focusedIndex);
    }
  };

  return (
    <div className="relative w-full max-w-lg">
      <Input
        ref={searchInputRef}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Search cards and notes... (Cmd+F)"
      />
      {results && (
        <SearchDropdown
          results={results}
          focusedIndex={focusedIndex}
          query={query}
        />
      )}
    </div>
  );
}
```

