Implementation pseudo-code:
```typescript
import Fuse from 'fuse.js';
import { useEffect, useState } from 'react';
import { useAppStore } from '@/stores/useAppStore';

interface Command {
  id: string;
  name: string;
  description: string;
  shortcut?: string;
  category: 'navigation' | 'action' | 'search';
  execute: () => void;
}

const COMMANDS: Command[] = [
  {
    id: 'nav-capture',
    name: 'Go to Capture',
    description: 'Navigate to capture interface',
    shortcut: 'Escape (from review)',
    category: 'navigation',
    execute: () => setCurrentView('capture')
  },
  // ... more commands
];

function CommandPalette() {
  const [open, setOpen] = useState(false);
  const [query, setQuery] = useState('');
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [recentCommands, setRecentCommands] = useState<string[]>([]);
  const { setCurrentView } = useAppStore();

  // Fuzzy search
  const fuse = new Fuse(COMMANDS, {
    keys: ['name', 'description'],
    threshold: 0.3
  });

  const filteredCommands = query
    ? fuse.search(query).map(result => result.item)
    : COMMANDS;

  // Global Cmd+K shortcut
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
        e.preventDefault();
        setOpen(true);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Load recent commands from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('doughub-recent-commands');
    if (stored) {
      setRecentCommands(JSON.parse(stored));
    }
  }, []);

  const executeCommand = (command: Command) => {
    command.execute();

    // Update recent commands
    const updated = [command.id, ...recentCommands.filter(id => id !== command.id)].slice(0, 5);
    setRecentCommands(updated);
    localStorage.setItem('doughub-recent-commands', JSON.stringify(updated));

    setOpen(false);
    setQuery('');
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedIndex((prev) => (prev + 1) % filteredCommands.length);
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedIndex((prev) => (prev - 1 + filteredCommands.length) % filteredCommands.length);
    } else if (e.key === 'Enter') {
      e.preventDefault();
      executeCommand(filteredCommands[selectedIndex]);
    } else if (e.key === 'Escape') {
      setOpen(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogContent className="max-w-lg">
        <Input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type a command or search..."
          autoFocus
        />

        {recentCommands.length > 0 && !query && (
          <div className="mb-2">
            <p className="text-xs text-muted-foreground mb-1">Recent</p>
            {recentCommands.slice(0, 3).map(id => {
              const cmd = COMMANDS.find(c => c.id === id);
              return cmd ? (
                <CommandItem
                  key={id}
                  command={cmd}
                  selected={false}
                  onClick={() => executeCommand(cmd)}
                />
              ) : null;
            })}
          </div>
        )}

        <ScrollArea className="max-h-96">
          {filteredCommands.map((command, index) => (
            <CommandItem
              key={command.id}
              command={command}
              selected={index === selectedIndex}
              onClick={() => executeCommand(command)}
            />
          ))}
          {filteredCommands.length === 0 && (
            <p className="text-center text-muted-foreground py-8">No commands found</p>
          )}
        </ScrollArea>
      </DialogContent>
    </Dialog>
  );
}
```

