Implementation pseudo-code:
```typescript
// electron/fsrs-analytics.ts
import { db } from './database';
import { FSRS, FSRSParameters } from 'ts-fsrs';

export interface DomainStats {
  domain: string;
  cardCount: number;
  avgResponseTime: number;
  accuracy: number;
  lastReviewed: string | null;
}

export interface ForgettingCurveData {
  days: number[];
  retention: number[];
}

export const fsrsAnalytics = {
  /**
   * Calculate personalized FSRS parameters for a card based on review history.
   */
  async calculatePersonalParams(cardId: string): Promise<FSRSParameters> {
    const reviews = db.prepare(
      'SELECT * FROM review_logs WHERE cardId = ? ORDER BY createdAt DESC LIMIT 10'
    ).all(cardId);

    if (reviews.length === 0) {
      // Return default params
      return new FSRS().params;
    }

    // Analyze review patterns
    const avgResponseTime = reviews.reduce((sum, r) => 
      sum + (r.responseTimeMs || 0), 0) / reviews.length;
    const lapseCount = reviews.filter(r => r.rating === 1).length;
    const lapseRate = lapseCount / reviews.length;

    // Adjust difficulty based on response time
    const baseDifficulty = 5; // Default from ts-fsrs
    const difficultyAdjustment = avgResponseTime > 20000 ? 1 : (avgResponseTime < 5000 ? -1 : 0);
    const difficulty = Math.max(1, Math.min(10, baseDifficulty + difficultyAdjustment));

    // Adjust stability based on lapse rate
    const baseStability = 1;
    const stabilityAdjustment = lapseRate > 0.3 ? -0.2 : (lapseRate < 0.1 ? 0.2 : 0);
    const stability = Math.max(0.1, baseStability + stabilityAdjustment);

    // Return personalized params
    const params = new FSRS().params;
    return {
      ...params,
      w: params.w.map((val, idx) => {
        // Adjust specific weight parameters based on analysis
        if (idx === 4) return difficulty; // Difficulty weight
        if (idx === 0) return stability;  // Initial stability
        return val;
      })
    };
  },

  /**
   * Get performance statistics for a medical domain.
   */
  async getDomainStats(domain: string): Promise<DomainStats> {
    // Get all cards with domain tag
    const cards = db.prepare(`
      SELECT c.*, rl.responseTimeMs, rl.rating
      FROM cards c
      LEFT JOIN review_logs rl ON rl.cardId = c.id
      WHERE json_extract(c.tags, '$[0]') = ?
    `).all(domain);

    if (cards.length === 0) {
      return {
        domain,
        cardCount: 0,
        avgResponseTime: 0,
        accuracy: 0,
        lastReviewed: null
      };
    }

    const uniqueCards = new Set(cards.map(c => c.id)).size;
    const reviewsWithTime = cards.filter(c => c.responseTimeMs !== null);
    const avgResponseTime = reviewsWithTime.length > 0
      ? reviewsWithTime.reduce((sum, c) => sum + c.responseTimeMs, 0) / reviewsWithTime.length
      : 0;

    // Accuracy: % of reviews rated Good or Easy (3 or 4)
    const reviewsWithRating = cards.filter(c => c.rating !== null);
    const accuracy = reviewsWithRating.length > 0
      ? reviewsWithRating.filter(c => c.rating >= 3).length / reviewsWithRating.length
      : 0;

    const lastReviewed = cards.reduce((latest, c) => {
      return c.lastReview && (!latest || c.lastReview > latest) ? c.lastReview : latest;
    }, null as string | null);

    return {
      domain,
      cardCount: uniqueCards,
      avgResponseTime: Math.round(avgResponseTime),
      accuracy: Math.round(accuracy * 100) / 100,
      lastReviewed
    };
  },

  /**
   * Calculate optimized retention rate based on user performance.
   */
  async getOptimizedRetention(): Promise<number> {
    const allReviews = db.prepare(
      'SELECT rating FROM review_logs ORDER BY createdAt DESC LIMIT 100'
    ).all();

    if (allReviews.length < 20) {
      return 0.89; // Default for medical education
    }

    const accuracy = allReviews.filter(r => r.rating >= 3).length / allReviews.length;

    // Adjust retention target based on accuracy
    if (accuracy > 0.92) {
      return 0.85; // User is doing well, can reduce review frequency
    } else if (accuracy < 0.80) {
      return 0.93; // User struggling, increase review frequency
    }

    return 0.89; // Keep default
  },

  /**
   * Detect cards that need reformulation (high response time, high lapses).
   */
  async detectCardsNeedingReformulation(): Promise<Array<{ card: DbCard; metrics: any }>> {
    const problematicCards = db.prepare(`
      SELECT c.*, 
        AVG(rl.responseTimeMs) as avgResponseTime,
        SUM(CASE WHEN rl.rating = 1 THEN 1 ELSE 0 END) as lapseCount,
        COUNT(rl.id) as reviewCount
      FROM cards c
      JOIN review_logs rl ON rl.cardId = c.id
      GROUP BY c.id
      HAVING avgResponseTime > 30000 OR lapseCount > 3
      ORDER BY avgResponseTime DESC
      LIMIT 20
    `).all();

    return problematicCards.map(row => ({
      card: parseCardRow(row),
      metrics: {
        avgResponseTime: row.avgResponseTime,
        lapseCount: row.lapseCount,
        reviewCount: row.reviewCount,
        suggestion: row.avgResponseTime > 30000
          ? 'Card takes too long to answer - consider splitting into multiple cards'
          : 'High lapse rate - card may need clarification or simplification'
      }
    }));
  },

  /**
   * Calculate forgetting curve for visualization.
   */
  async getForgettingCurve(domain?: string): Promise<ForgettingCurveData> {
    const query = domain
      ? `SELECT c.*, rl.elapsedDays, rl.rating
         FROM cards c
         JOIN review_logs rl ON rl.cardId = c.id
         WHERE json_extract(c.tags, '$[0]') = ?
         ORDER BY rl.elapsedDays`
      : `SELECT rl.elapsedDays, rl.rating
         FROM review_logs rl
         ORDER BY rl.elapsedDays`;

    const reviews = domain
      ? db.prepare(query).all(domain)
      : db.prepare(query).all();

    // Group by elapsed days, calculate retention rate
    const dayGroups = new Map<number, { total: number; recalled: number }>();

    reviews.forEach(r => {
      const day = Math.floor(r.elapsedDays);
      if (!dayGroups.has(day)) {
        dayGroups.set(day, { total: 0, recalled: 0 });
      }
      const group = dayGroups.get(day)!;
      group.total++;
      if (r.rating >= 3) {
        group.recalled++;
      }
    });

    const days: number[] = [];
    const retention: number[] = [];

    dayGroups.forEach((group, day) => {
      days.push(day);
      retention.push(group.recalled / group.total);
    });

    return { days, retention };
  }
};
