VALIDATION LIBRARY IMPLEMENTATION:
```typescript
// src/lib/card-validation.ts
export function validateCard(card: { front: string; back: string }): ValidationResult {
  const issues: ValidationIssue[] = [];

  // Check empty fields
  if (!card.front.trim() || !card.back.trim()) {
    issues.push({
      type: 'empty-field',
      severity: 'error',
      message: 'Both question and answer must be filled',
      suggestion: undefined
    });
  }

  // Check pattern matching
  const patternMatching = detectPatternMatching(card);
  if (patternMatching) {
    issues.push(patternMatching);
  }

  // Check multi-fact
  const multiFact = detectMultiFact(card);
  if (multiFact) {
    issues.push(multiFact);
  }

  // Check medical list
  const medicalList = detectMedicalList(card);
  if (medicalList) {
    issues.push(medicalList);
  }

  // Check minimum information
  const minInfo = validateMinimumInformation(card);
  if (minInfo) {
    issues.push(minInfo);
  }

  return {
    isValid: !issues.some(i => i.severity === 'error'),
    issues
  };
}

function detectPatternMatching(card: { front: string; back: string }): ValidationIssue | null {
  const patterns = [
    /which of the following/i,
    /select the correct/i,
    /choose the best/i,
    /\([A-D]\)/,  // Multiple choice markers
    /^[A-D]\./m   // Multiple choice format
  ];

  const hasPattern = patterns.some(pattern => pattern.test(card.front));

  if (hasPattern) {
    return {
      type: 'pattern-matching',
      severity: 'error',
      message: 'Card contains pattern-matching cues (multiple choice format)',
      suggestion: 'Rephrase as direct question without multiple choice format',
      autoFix: undefined // Could add AI-powered rephrasing
    };
  }

  return null;
}

function detectMultiFact(card: { front: string; back: string }): ValidationIssue | null {
  // Check for multiple facts in answer
  const indicators = [
    /\d+\.\s/g,        // Numbered lists
    /;/g,              // Semicolons
    / and /gi,         // 'and' conjunction
    /also/gi,          // 'also'
    /additionally/gi   // 'additionally'
  ];

  const matches = indicators.reduce((count, pattern) => {
    const found = card.back.match(pattern);
    return count + (found ? found.length : 0);
  }, 0);

  if (matches >= 2) {
    // Suggest split
    const autoFix = () => {
      // Simple split by sentences or numbered items
      const sentences = card.back.split(/[.!?]/).filter(s => s.trim());
      return sentences.map((sentence, idx) => ({
        front: `${card.front} (Part ${idx + 1})`,
        back: sentence.trim()
      }));
    };

    return {
      type: 'multi-fact',
      severity: 'warning',
      message: `Card appears to test multiple facts (${matches} indicators found)`,
      suggestion: `Consider splitting into ${Math.min(matches, 5)} separate cards`,
      autoFix
    };
  }

  return null;
}

function detectMedicalList(card: { front: string; back: string }): ValidationIssue | null {
  const listPatterns = [
    /\d+\.\s.+/,      // Numbered list
    /causes?:/i,      // 'causes:'
    /symptoms?:/i,    // 'symptoms:'
    /signs?:/i,       // 'signs:'
    /differential/i   // 'differential'
  ];

  const isList = listPatterns.some(pattern => pattern.test(card.back));
  const hasContext = /patient|presents|history|exam/i.test(card.front);

  if (isList && !hasContext) {
    return {
      type: 'medical-list',
      severity: 'warning',
      message: 'Medical list detected without clinical context',
      suggestion: 'Convert to clinical vignettes for better retention',
      autoFix: undefined // Handled by medical list processor
    };
  }

  return null;
}

function validateMinimumInformation(card: { front: string; back: string }): ValidationIssue | null {
  // This is covered by multi-fact detection
  return null;
}
