## Phase 1: Local HTTP API Server in Electron Main Process

### 1.1 Create HTTP Server Module (electron/api-server.ts)
```typescript
import { createServer, IncomingMessage, ServerResponse } from 'http';
import { randomUUID } from 'crypto';
import { sourceItemQueries, DbSourceItem } from './database';

const API_PORT = 31337;
const ALLOWED_ORIGINS = ['chrome-extension://*', 'moz-extension://*'];

interface CapturePayload {
  content: string;
  url: string;
  title: string;
  sourceType: 'webcapture';
  tags?: string[];
  selectedText?: string;
  pageHtml?: string;
}

export function startApiServer() {
  const server = createServer(handleRequest);

  server.listen(API_PORT, 'localhost', () => {
    console.log(`[API Server] Listening on localhost:${API_PORT}`);
  });

  return server;
}

async function handleRequest(req: IncomingMessage, res: ServerResponse) {
  // CORS headers for browser extensions
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'POST, GET, OPTIONS');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

  if (req.method === 'OPTIONS') {
    res.writeHead(200);
    res.end();
    return;
  }

  // Health check endpoint
  if (req.method === 'GET' && req.url === '/api/health') {
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(JSON.stringify({ status: 'ok', version: '1.0.0' }));
    return;
  }

  // Capture endpoint
  if (req.method === 'POST' && req.url === '/api/capture') {
    let body = '';
    req.on('data', chunk => body += chunk);
    req.on('end', () => {
      try {
        const payload: CapturePayload = JSON.parse(body);
        const sourceItem: DbSourceItem = {
          id: randomUUID(),
          sourceType: 'webcapture',
          sourceName: new URL(payload.url).hostname,
          sourceUrl: payload.url,
          title: payload.title || 'Untitled Capture',
          rawContent: payload.content,
          canonicalTopicIds: [],
          tags: payload.tags || [],
          status: 'inbox',
          createdAt: new Date().toISOString(),
        };

        sourceItemQueries.insert(sourceItem);

        res.writeHead(201, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ success: true, id: sourceItem.id }));
      } catch (error) {
        res.writeHead(400, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ success: false, error: String(error) }));
      }
    });
    return;
  }

  res.writeHead(404);
  res.end('Not found');
}
```

