// Capture content to DougHub
async function captureContent(data) {
  try {
    const response = await fetch(DOUGHUB_API, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    });

    if (response.ok) {
      chrome.notifications.create({
        type: 'basic',
        iconUrl: 'icons/icon48.png',
        title: 'DougHub',
        message: 'Content saved successfully!'
      });
    } else {
      throw new Error('API request failed');
    }
  } catch (error) {
    // Queue for later
    captureQueue.push(data);
    chrome.storage.local.set({ captureQueue });

    chrome.notifications.create({
      type: 'basic',
      iconUrl: 'icons/icon48.png',
      title: 'DougHub Offline',
      message: 'Content queued for later sync'
    });
  }
}

// Retry queue every 30 seconds
setInterval(async () => {
  if (captureQueue.length > 0) {
    try {
      const health = await fetch('http://localhost:31337/api/health');
      if (health.ok) {
        while (captureQueue.length > 0) {
          const item = captureQueue.shift();
          await captureContent(item);
        }
        chrome.storage.local.set({ captureQueue: [] });
      }
    } catch {}
  }
}, 30000);
